package com.hibernate.jdbc.jdbc;import jakarta.persistence.*;import lombok.*;import org.hibernate.annotations.JdbcTypeCode;import org.hibernate.type.SqlTypes;import java.util.Date;@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Entity(name = "PERSON")public class Person {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "id", nullable = false)    private int id;    private String name;    private String location;    @Column(name = "birth_date")    private Date birthDate;    @Override    public String toString() {        return "Person{" +                "id=" + id +                ", name='" + name + '\'' +                ", localtion='" + location + '\'' +                ", birthDate=" + birthDate +                '}';    }}